INSERT INTO `ministries` (`id`, `initials`, `name`, `logo`, `color`, `description`, `created_at`, `updated_at`) VALUES
	(1, 'ev²', 'Evangelismo Eficaz', NULL, NULL, 'O Ministério Evangelismo Eficaz tem como propósito capacitar cristãos a compartilhar o evangelho de Jesus Cristo de forma clara, bíblica e intencional no dia a dia. Por meio de treinamentos práticos e fundamentados nas Escrituras, o ministério promove uma vida de testemunho fiel, preparando a igreja para cumprir a Grande Comissão com convicção, amor e simplicidade.', '2026-02-02 08:26:25', '2026-02-02 08:26:25'),
	(2, 'ee-kids', 'EE-Kids', NULL, NULL, 'O EE-Kids é o ministério voltado à evangelização de crianças, ensinando desde cedo as verdades fundamentais do evangelho de forma didática, bíblica e adequada à faixa etária. Seu objetivo é auxiliar pais, educadores e igrejas na formação espiritual das crianças, cultivando uma fé sólida e um coração disposto a conhecer e seguir a Cristo.', '2026-02-02 08:26:25', '2026-02-02 08:26:25');
